import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { products } from '../productData';
import '../App.css';


function Home() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('mesh');

  const meshProducts = products.filter(p => p.title.toLowerCase().includes('mesh') || p.title.toLowerCase().includes('grill'));
  const blindsProducts = products.filter(p => p.title.toLowerCase().includes('blind'));

  return (
    <div className="home-split">
      {/* Top section: Carousel */}
      <div className="home-carousel">
        <div className="carousel-track">
          {products.map((product, idx) => (
            <img
              key={idx}
              src={product.mainImage}
              alt={product.title}
              className="carousel-img"
            />
          ))}
        </div>
      </div>
      {/* Bottom section: Tabs for product groups */}
      <div className="home-product-tabs" style={{display: 'flex', gap: 24, marginBottom: 16}}>
        <div
          className={activeTab === 'mesh' ? 'tab-title active' : 'tab-title'}
          style={{cursor: 'pointer', fontWeight: 600, fontSize: '1.2rem', borderBottom: activeTab === 'mesh' ? '2px solid #2d3e50' : '2px solid transparent', paddingBottom: 4}}
          onClick={() => setActiveTab('mesh')}
        >
          Mesh and Grill Products
        </div>
        <div
          className={activeTab === 'blinds' ? 'tab-title active' : 'tab-title'}
          style={{cursor: 'pointer', fontWeight: 600, fontSize: '1.2rem', borderBottom: activeTab === 'blinds' ? '2px solid #2d3e50' : '2px solid transparent', paddingBottom: 4}}
          onClick={() => setActiveTab('blinds')}
        >
          Blinds Products
        </div>
      </div>
      <div className="home-product-list">
        {activeTab === 'mesh' && meshProducts.map((product, idx) => (
          <div
            key={product.folder}
            className="product-card"
            style={{cursor: 'default'}}
          >
            <img
              src={product.mainImage}
              alt={product.title}
              className="product-thumb"
              style={{cursor: 'pointer'}}
              onClick={() => navigate(`/products/${product.folder}`)}
            />
            <div className="product-title">{product.title}</div>
            <div
              className="product-more-link"
              style={{color: '#2d3e50', fontSize: '0.95rem', marginTop: 4, textDecoration: 'underline', fontWeight: 500, cursor: 'pointer'}}
              onClick={() => navigate(`/products/${product.folder}`)}
            >
              Click for more images
            </div>
          </div>
        ))}
        {activeTab === 'blinds' && blindsProducts.map((product, idx) => (
          <div
            key={product.folder}
            className="product-card"
            style={{cursor: 'default'}}
          >
            <img
              src={product.mainImage}
              alt={product.title}
              className="product-thumb"
              style={{cursor: 'pointer'}}
              onClick={() => navigate(`/products/${product.folder}`)}
            />
            <div className="product-title">{product.title}</div>
            <div
              className="product-more-link"
              style={{color: '#2d3e50', fontSize: '0.95rem', marginTop: 4, textDecoration: 'underline', fontWeight: 500, cursor: 'pointer'}}
              onClick={() => navigate(`/products/${product.folder}`)}
            >
              Click for more images
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
