import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { mesh_products, blinds_products } from '../productData';

const ProductDetail = () => {
  const { productFolder } = useParams();
  let product = mesh_products.find(p => p.folder === productFolder);
  if (!product) {
    product = blinds_products.find(p => p.folder === productFolder);
  }
  const images = product?.images || [];
  const description = product?.description || {};

  return (
    <div>
      <Link to="/">‚Üê Back to Home</Link>
      <h2>Product Gallery</h2>
      <div style={{display: 'flex', flexDirection: 'row', alignItems: 'stretch', gap: '2.5rem', marginTop: '1.5rem'}}>
        {/* Images vertically */}
        <div id="product-images-col" style={{display: 'flex', flexDirection: 'column', gap: '1.2rem', minWidth: 220, width: 220}}>
          {images.length === 0 && <p>No additional images found for this product.</p>}
          {images.map((img, idx) => (
            <div key={idx} style={{margin: 0, display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
              <img src={img} alt={`Product ${idx + 1}`} className="product-gallery-img" style={{width: 200, height: 'auto', borderRadius: 8, boxShadow: '0 1px 6px #c3cfe2'}} />
            </div>
          ))}
        </div>
        {/* Description beside images, height matches images column */}
        <div style={{
          minWidth: 220,
          width: 220,
          fontSize: '1.05rem',
          background: '#f8f9fa',
          borderRadius: 8,
          padding: '1.2rem 1.5rem',
          boxShadow: '0 1px 6px #e0e7ef',
          border: '1px solid #e0e7ef',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-start',
          height: 'auto',
          alignSelf: 'stretch'
        }}>
          <h3 style={{marginTop: 0, marginBottom: '1.2rem', fontSize: '1.18rem', color: '#1976d2'}}>Product Details</h3>
          <table style={{width: '100%', borderCollapse: 'collapse'}}>
            <tbody>
              {Object.entries(description).map(([key, value]) => (
                <tr key={key}>
                  <td style={{fontWeight: 500, color: '#888', padding: '0.3rem 0.7rem 0.3rem 0', verticalAlign: 'top', whiteSpace: 'nowrap'}}>{key}</td>
                  <td style={{fontWeight: 400, color: '#222', padding: '0.3rem 0 0.3rem 0.5rem'}}>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
