import React from 'react';
import { useParams, Link } from 'react-router-dom';

function importAll(r) {
  return r.keys().map(r);
}

const ProductDetail = () => {
  const { productFolder } = useParams();
  // Import all images in the product folder except main.jpg
  let images = [];
  try {
    images = importAll(
      require.context(`../products/${productFolder}`, false, /^(?!.*main\.jpg$).*\.(jpg|png|svg)$/)
    );
  } catch (e) {
    images = [];
  }

  return (
    <div>
      <Link to="/">‚Üê Back to Home</Link>
      <h2>Product Gallery</h2>
      <div style={{display: 'flex', flexWrap: 'wrap', gap: '1rem'}}>
        {images.length === 0 && <p>No additional images found for this product.</p>}
        {images.map((img, idx) => (
          <img key={idx} src={img.default} alt="Product" className="product-gallery-img" />
        ))}
      </div>
    </div>
  );
};

export default ProductDetail;
