import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { products } from '../productData';

const ProductDetail = () => {
  const { productFolder } = useParams();
  const product = products.find(p => p.folder === productFolder);
  const images = product?.images || [];

  return (
    <div>
      <Link to="/">‚Üê Back to Home</Link>
      <h2>Product Gallery</h2>
      <div style={{display: 'flex', flexWrap: 'wrap', gap: '1rem'}}>
        {images.length === 0 && <p>No additional images found for this product.</p>}
        {images.map((img, idx) => (
          <img key={idx} src={img} alt="Product" className="product-gallery-img" />
        ))}
      </div>
    </div>
  );
};

export default ProductDetail;
